INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47806, 11214, 'Docetaxel', 'Alcoholism', 'The alcohol content in a dose of docetaxel injection may affect the central nervous system.  Cases of intoxication have been reported with some formulations of docetaxel due to the alcohol content.  Close monitoring is recommended in alcoholic patients.  Consideration should be given to the alcohol content in docetaxel injection on the ability to drive or use machines immediately after the infusion.', '2', '"Product Information. Taxotere (docetaxel)." Rhone Poulenc Rorer  (2001):', 'DDInter', 0);
