INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47819, 11214, 'Docetaxel', 'Paresthesia', 'Severe neurosensory symptoms such as paresthesia, dysesthesia, and pain have been reported during docetaxel therapy.  Therapy with docetaxel should be administered cautiously to patients with or predisposition to neurosensory symptoms.  Although reversible with discontinuation of docetaxel therapy, the dosage must be adjusted if neurosensory symptoms occur and therapy discontinued if symptoms persist.', '2', 'New PZ, Jackson CE, Rinaldi D, Burris H, Barohn RJ "Peripheral neuropathy secondary to docetaxel (Taxotere)." Neurology 46 (1996):  108-11|New P "Neurotoxicity of Taxotere (Meeting abstract)." Proc Annu Meet Am Assoc Cancer Res 34 (1993):  a13931993|"Product Information. Taxotere (docetaxel)." Rhone Poulenc Rorer  (2001):', 'DDInter', 0);
