INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47870, 6907, 'Doxorubicin', 'Liver Diseases', 'Doxorubicin is contraindicated in patients with severe hepatic impairment (defined as Child Pugh Class C or serum bilirubin level greater than 5 mg/dL).  Doxorubicin is primarily metabolized by the liver and its clearance is reduced in the presence of hepatic dysfunction, increasing the risk for toxicity.  Therapy with doxorubicin should be administered cautiously and at a reduced dosage in patients with moderate or mild hepatic function.  Close clinical monitoring of hepatic function prior to initiation of doxorubicin therapy is recommended.', '3', 'Aviles A, Herrera J, Ramos E, Ambriz R, Aguirre J, Pizzuto J "Hepatic injury during doxorubicin therapy." Arch Pathol Lab Med 108 (1984):  912-3|Maral RJ, Jouanne M "Toxicology of daunorubicin in animals and man." Cancer Treat Rep 65 Suppl 4 (1981):  9-18|"Product Information. Adriamycin PFS (doxorubicin)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
