INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48539, 30437, 'Dupilumab', 'Helminthiasis', 'Patients with preexisting-existing helminth infections should be treated prior to therapy with dupilumab.  It is recommended to discontinue treatment with dupilumab if patients become infected while on treatment and if they are not responding to treatment.  Discontinue treatment with dupilumab until the infection resolves.', '2', '"Product Information. Dupixent (dupilumab)." sanofi-aventis  (2017):', 'DDInter', 0);
