INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48543, 30670, 'Durvalumab', 'Adrenal Insufficiency', 'Durvalumab can cause immune-mediated adrenal insufficiency.  Adrenal function should be monitored in patients with adrenal insufficiency or with borderline adrenal function and in patients with prolonged periods of stress due to major surgery, intensive care, etc.  It is recommended to monitor patients for clinical signs and symptoms of adrenal insufficiency and to institute appropriate measures as necessary.  Monitor as clinically indicated prior to and periodically during treatment and withhold, reduce dose, or discontinue therapy with durvalumab as necessary.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
