INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48547, 30670, 'Durvalumab', 'Diabetes Mellitus', 'Durvalumab can cause type 1 diabetes mellitus.  Monitor for hyperglycemia or other signs and symptoms of diabetes.  Withhold treatment in cases of severe hyperglycemia until metabolic control is achieved.  Permanently discontinue durvalumab for life-threatening hyperglycemia.  If appropriate interrupt treatment according to manufacturer recommendations.  Care should be exercised when using durvalumab in diabetic patients.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48548, 30671, 'Durvalumab', 'Diabetes Mellitus', 'Durvalumab can cause type 1 diabetes mellitus.  Monitor for hyperglycemia or other signs and symptoms of diabetes.  Withhold treatment in cases of severe hyperglycemia until metabolic control is achieved.  Permanently discontinue durvalumab for life-threatening hyperglycemia.  If appropriate interrupt treatment according to manufacturer recommendations.  Care should be exercised when using durvalumab in diabetic patients.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48549, 30670, 'Durvalumab', 'Infections', 'Durvalumab may alter the normal immune response and increase susceptibility to infections.  Monitor patients for signs and symptoms of infection and withhold durvalumab treatment for Grade 3 or higher infections, particularly in patients with urinary tract infections.  Resume once clinically stable.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
