INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48553, 30670, 'Durvalumab', 'Kidney Diseases', 'Durvalumab can cause immune-mediated nephritis.  Monitor patients for abnormal renal function tests prior to and periodically during treatment.  It is recommended to administer corticosteroids at a dose of 1 to 2 mg/kg/day prednisone equivalents followed by corticosteroid taper for life-threatening increased serum creatinine.  Withhold or permanently discontinue durvalumab based on severity.  If appropriate modify the dose according to manufacturer recommendations.  Care should be taken when using durvalumab in patients with renal dysfunction.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
