INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48555, 30670, 'Durvalumab', 'Thyroid Diseases', 'Durvalumab can cause immune-mediated thyroid disorders.  Monitor thyroid function prior to and periodically during treatment.  Administer hormone-replacement therapy for hypothyroidism or initiate medical management for control of hyperthyroidism.  Continue durvalumab for hypothyroidism and interrupt for hyperthyroidism based on the manufacturer recommendation.  Care should be taken when using this agent in patients with thyroid disease.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
