INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48565, 29123, 'Dyphylline', 'Peptic Ulcer', 'Methylxanthines are known to stimulate peptic acid secretion.  Therapy with products containing methylxanthines should be administered with extreme caution in patients with active peptic ulcer disease.  Some manufacturers consider their use to be contraindicated under such circumstance.', '3', 'Stoller JL "Oesophageal ulceration and theophylline." Lancet 2 (1985):  328-9|"Product Information. Theo-Dur (theophylline)." Schering Corporation  (2001):|Alterman P, Spiegel D, Feldman J, Yaretzky A "Histamine h2-receptor antagonists and chronic theophylline toxicity." Am Fam Physician 54 (1996):  1473|"Product Information. Lufyllin (dyphylline)." Wallace Laboratories  (2001):', 'DDInter', 0);
