INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48622, 25974, 'Eltrombopag', 'Hepatitis C', 'Eltrombopag has shown to increase the risk of hepatic decompensation in patients with chronic hepatitis C in treatment with interferon and ribavirin.  Clinical trials reported that ascites and encephalopathy occurred more frequently on these patients than in the placebo arm.  Patients with chronic hepatitis and receiving antiviral treatment should be closely monitored.  The manufacturers also recommend to discontinue eltrombopag if the antiviral therapy is discontinued.', '3', '"Product Information. Promacta (eltrombopag)." GlaxoSmithKline  (2008):', 'DDInter', 0);
