INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48628, 25974, 'Eltrombopag', 'Cataract', 'In clinical trials, eltrombopag developed or worsened cataracts in 7% of the patients receiving treatment.  A baseline ocular examination prior to administration of eltrombopag is recommended, and regular monitoring for signs and symptoms of cataracts should be performed regularly during treatment.', '2', '"Product Information. Promacta (eltrombopag)." GlaxoSmithKline  (2008):', 'DDInter', 0);
