INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48855, 4460, 'Enfortumab vedotin', 'Hyperglycemia', 'Hyperglycemia occurred in patients treated with enfortumab vedotin, including death, and diabetic ketoacidosis (DKA) in those with and without preexisting-existing diabetes mellitus.  The incidence of Grade 3-4 hyperglycemia increased consistently in patients with higher body mass index and in patients with higher baseline A1C.  Closely monitor blood glucose levels in patients with, or at risk for, diabetes mellitus or hyperglycemia.  If blood glucose is elevated (>250 mg/dL), withhold treatment.', '2', '"Product Information. Padcev (enfortumab vedotin)." Seattle Genetics Inc  (2019):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48856, 4463, 'Enfortumab vedotin', 'Hyperglycemia', 'Hyperglycemia occurred in patients treated with enfortumab vedotin, including death, and diabetic ketoacidosis (DKA) in those with and without preexisting-existing diabetes mellitus.  The incidence of Grade 3-4 hyperglycemia increased consistently in patients with higher body mass index and in patients with higher baseline A1C.  Closely monitor blood glucose levels in patients with, or at risk for, diabetes mellitus or hyperglycemia.  If blood glucose is elevated (>250 mg/dL), withhold treatment.', '2', '"Product Information. Padcev (enfortumab vedotin)." Seattle Genetics Inc  (2019):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48857, 4460, 'Enfortumab vedotin', 'Peripheral Nervous System Diseases', 'Peripheral neuropathy, predominantly sensory, occurred in 49% of the patients treated with enfortumab vedotin in clinical trials.  Peripheral neuropathy occurred in patients with or without preexisting peripheral neuropathy.  Monitor patients for symptoms of new or worsening peripheral neuropathy and consider dose interruption or dose reduction when peripheral neuropathy occurs.  Permanently discontinue treatment in patients that develop Grade >3 peripheral neuropathy.', '2', '"Product Information. Padcev (enfortumab vedotin)." Seattle Genetics Inc  (2019):', 'DDInter', 0);
