INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49015, 16696, 'Entacapone', 'Hepatic Insufficiency', 'Entacapone should be used with caution in patients with hepatic impairment, as the AUC and Cmax of the drug approximately doubled in patients with documented liver disease compared to patients with normal liver function.', '2', '"Product Information. Comtan (entacapone)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
