INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49025, 21810, 'Epinephrine', 'Dehydration', 'The use of sympathomimetic amines has been infrequently associated with significant hypotension especially in dehydrated patients secondary to the drug''s beta-2 mediated vasodilation.  Hypovolemia should be corrected, if possible, before administering sympathomimetic amines.  Blood pressure and ECG should be monitored at regular intervals.  Monitoring of cardiac output and pulmonary wedge pressure may also be desired.', '3', '"Product Information. Isuprel (isoproterenol)." Sanofi Winthrop Pharmaceuticals  (2001):|"Product Information. Epifrin (EPINEPHrine ophthalmic)." Allergan Inc  (2022):|"Product Information. Adrenalin (EPINEPHrine)." Apothecon Inc  (2022):|"Product Information. Levophed Bitartrate (norepinephrine)." Sanofi Winthrop Pharmaceuticals  (2001):', 'DDInter', 0);
