INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49031, 21810, 'Epinephrine', 'Diabetes Mellitus', 'Sympathomimetic agents may cause increases in blood glucose concentrations.  These effects are usually transient and slight but may be significant with dosages higher than those normally recommended.  Therapy with sympathomimetic agents should be administered cautiously in patients with diabetes mellitus.  Closer monitoring of blood glucose concentrations may be appropriate.', '2', 'Covington TR, eds., Lawson LC, Young LL "Handbook of Nonprescription Drugs." Washington, DC: American Pharmaceutical Association  (1993):|"Product Information. Sudafed (pseudoephedrine)." Glaxo Wellcome  (2001):|American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):|Williams DM "Phenylpropanolamine hydrochloride" Am Pharm NS30 (1990):  47-50|"Product Information. Adrenalin (EPINEPHrine)." Apothecon Inc  (2022):|"Product Information. Akovaz (ephedrine)." Eclat Pharmaceuticals  (2016):', 'DDInter', 0);
