INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49033, 6527, 'Eptifibatide', 'Kidney Diseases', 'The use of eptifibatide is contraindicated in patients dependent of dialysis.  Approximately 50% of eptifibatide is cleared by the kidney in patients with normal renal function.  Total drug clearance is decreased by approximately 50% and steady-state plasma eptifibatide concentrations are doubled in patients with an estimated CrCl <50 mL/min.  Therefore, the infusion dose should be reduced to 1 mcg/kg/min in such patients Clinical monitoring of renal function is recommended.', '3', '"Product Information. Integrilin (eptifibatide)." Schering Corporation  (2001):', 'DDInter', 0);
