INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49105, 22120, 'Erlotinib', 'Lung Diseases', 'The use of certain EGFR inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.', '3', '"Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals  (2003):|"Product Information. Erbitux (cetuximab)." Bristol-Myers Squibb  (2004):|"Product Information. Tarceva (erlotinib)." Genentech  (2004):|"Product Information. Vectibix (panitumumab)." Amgen USA  (2006):|"Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals  (2007):|"Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals  (2015):', 'DDInter', 0);
