INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49414, 527, 'Amikacin (liposome)', 'Lung Diseases', 'Higher frequency of exacerbations of underlying pulmonary disease has been reported with amikacin liposome treatment.  Monitor closely patients with pulmonary impairment and treat as medically appropriate if this occurs during treatment with amikacin liposome.  Pre- treatment with short-acting selective beta- 2 agonists should be considered for patients with known hyperreactive airway disease, chronic obstructive pulmonary disease, asthma, or bronchospasm.', '2', '"Product Information. Arikayce (amikacin liposome)." Insmed Incorporated  (2018):', 'DDInter', 0);
