INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50636, 4440, 'Ethosuximide', 'Liver Diseases', 'Succinimide anticonvulsants have produced morphological and functional changes in the liver of animals.  In humans, abnormalities in liver and renal function studies have been reported.  Therapy with succinimide anticonvulsants should be administered with extreme caution in patients with known hepatic or renal diseases.  Periodic urinalysis and liver function tests are recommended for all patients treated with these drugs.', '3', 'Silverman SH, Gribetz D, Rausen AR "Nephrotic syndrome associated with ethosuccimide." Am J Dis Child 132 (1978):  99|Coulter DL "Ethosuximide-induced liver dysfunction." Arch Neurol 40 (1983):  393-4|Korinthenberg R, Wehrle L, Zimmerhackl LB "Renal tubular dysfunction following treatment with anti-epileptic drugs." Eur J Pediatr 153 (1994):  855-8|"Product Information. Zarontin (ethosuximide)." Parke-Davis  (2001):|"Product Information. Milontin (phensuximide)." Parke-Davis  (2001):|"Product Information. Celontin (methsuximide)." Parke-Davis', 'DDInter', 0);
