INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50644, 29189, 'Abemaciclib', 'Liver Diseases', 'The dosing frequency of abemaciclib should be reduced to once daily when it is administered to patients with severe hepatic impairment (Child-Pugh C).  No dosage adjustments are necessary for patients with mild or moderate hepatic impairment (Child-Pugh A or B).  Hepatotoxicity has been reported with the use of abemaciclib.  It is recommended to monitor liver function tests prior to the start of therapy, every 2 weeks for the first 2 months, monthly for the next 2 months, and as clinically indicated.  Dose interruption, dose reduction, dose discontinuation, or delay in starting treatment cycles is recommended for patients who develop persistent or recurrent Grade 2, or Grade 3 or 4, hepatic transaminase elevation.', '3', '"Product Information. Verzenio (abemaciclib)." Lilly, Eli and Company  (2017):', 'DDInter', 0);
