INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50828, 958, 'Exemestane', 'Osteoporosis', 'Reductions in bone mineral density (BMD) over time are seen with the use of exemestane.  Women with osteoporosis or at risk of osteoporosis should have their BMD formally assessed by densitometry at treatment initiation.  Monitoring for BMD loss is recommended, and treatment for it should be started as appropriate.', '2', '"Product Information. Aromasin (exemestane)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50829, 958, 'Exemestane', 'Vitamin D Deficiency', 'Routine assessment of vitamin D levels prior to the start of aromatase inhibitor treatment should be performed, due to the high prevalence of vitamin D deficiency in women with early breast cancer.  Women with vitamin D deficiency should receive supplementation.', '2', '"Product Information. Aromasin (exemestane)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
