INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50911, 2521, 'Ezetimibe', 'Kidney Diseases', 'No dosage adjustment of ezetimibe is necessary in patients with renal impairment.  However, when ezetimibe therapy is given in combination with simvastatin in patients with moderate to severe renal impairment (estimated glomerular filtration rate <60 mL/min/1.73 m2), doses of simvastatin exceeding 20 mg should be used with caution and close monitoring is recommended.', '2', '"Product Information. Zetia (ezetimibe)." Schering-Plough Corporation  (2002):', 'DDInter', 0);
