INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52101, 3, 'Fluorouracil', 'Hemorrhage', 'Fluorouracil (5- FU) induces myelosuppression, including thrombocytopenia.  Therapy with 5- FU should be administered cautiously in patients with bleeding tendencies.  Therapy with 5- FU should be promptly discontinued following bleeding from any sight.  Patients should be instructed to immediately report any signs or symptoms suggesting bleeding such as petechiae, purpura, hematuria, or melena.  Close clinical monitoring of hematopoietic function is recommended.  Therapy with 5- FU should be withheld if platelet counts falls below 100,000/mm3.', '2', '"Product Information. Fluorouracil (fluorouracil)." Roche', 'DDInter', 0);
