INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52385, 2064, 'Amiodarone', 'Vision Disorders', 'Optic neuropathy and/or neuritis has occurred during administration of amiodarone and has resulted in visual impairment such as changes in visual acuity, decrease in peripheral vision, and blindness.  Optic toxicity can occur at any time following initiation of amiodarone.  Therapy with amiodarone should be administered cautiously in patients with visual defects.  Regular ophthalmologic examinations including fundoscopy and slit- lamp examinations are recommended.', '3', 'Imgram DV, Jaggarao NS, Chamberlain DA "Ocular changes resulting from therapy with amiodarone." Br J Ophthalmol 66 (1982):  676-9|Feiner LA, Younge BR, Kazmier FJ, Stricker BH, Fraunfelder FT "Optic neuropathy and amiodarone therapy." Mayo Clin Proc 62 (1987):  702-17|"Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories  (2002):|Thystrup JD, Fledelius HC "Retinal maculopathy possibly associated with amiodarone medication." Acta Ophthalmol (Copenh) 72 (1994):  639-41|"Product Information. Cordarone (amiodarone)." Apothecon Inc  (2022):', 'DDInter', 0);
