INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54476, 489, 'Glimepiride', 'Hyponatremia', 'Treatment with sulfonylureas may cause hyponatremia, in patients who are on other medications or have medical conditions known to cause hyponatremia or increase release of antidiuretic hormone.  The syndrome of inappropriate antidiuretic hormone (SIADH) secretion has been reported with certain sulfonylureas and these drugs may augment the peripheral (antidiuretic) action of ADH and/or increase release of ADH.  Caution should be used when treating patients with hyponatremia or at greater risk of developing hyponatremia such as elderly patients, patients taking diuretics or those who are volume-depleted.', '2', '"Product Information. Diabinese (chlorpropamide)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Glucotrol (glipizide)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.|"Product Information. Micronase (glyburide)." Pharmacia and Upjohn  (2002):|"Product Information. Amaryl (glimepiride)." Hoechst Marion Roussel  (2001):|"Product Information. Tolinase (tolazamide)." Pharmacia and Upjohn  (2001):|"Product Information. Orinase (tolbutamide)." Pharmacia and Upjohn  (2001):|"Product Information. Dymelor (acetohexamide)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
