INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54531, 19851, 'Glipizide', 'Diabetic Ketoacidosis', 'The use of some sulfonylurea agents is contraindicated for the treatment of patients with diabetic ketoacidosis, with or without coma.  In addition, these agents should not be used as sole therapy in patients with type I diabetes mellitus.', '3', '"Product Information. Diabinese (chlorpropamide)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Glucotrol (glipizide)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.|"Product Information. Micronase (glyburide)." Pharmacia and Upjohn  (2002):|"Product Information. Amaryl (glimepiride)." Hoechst Marion Roussel  (2001):|"Product Information. Tolinase (tolazamide)." Pharmacia and Upjohn  (2001):|"Product Information. Orinase (tolbutamide)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
