INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54603, 277, 'Amoxicillin', 'Infectious Mononucleosis', 'Patients with mononucleosis treated with an aminopenicillin antibiotic, may develop a pruritic erythematous maculopapular skin rash.  The rash is usually self-limiting and resolves within days of discontinuing the offending agent.  An altered drug metabolism or an immune-mediated process unrelated to drug hypersensitivity has been proposed as the underlying mechanism.  Therapy with aminopenicillin antibiotics should not be administered in patients with mononucleosis.', '2', '"Product Information. Polycillin-PRB (ampicillin-probenecid)." Apothecon Inc|"Product Information. Spectrobid (bacampicillin)." Roerig Division  (2002):|"Product Information. Amoxil (amoxicillin)." SmithKline Beecham  (2001):', 'DDInter', 0);
