INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (54894, 5744, 'Griseofulvin', 'Lupus Erythematosus, Systemic', 'The use of griseofulvin has been associated with the development of lupus erythematosus and lupus-like syndromes, as well as exacerbation of the disease.  Griseofulvin therapy should be withdrawn in patients experiencing worsening of preexisting lupus until another cause can be identified.', '2', 'Madhok R, Zoma A, Capell H "Fatal exacerbation of systemic lupus erythematosus after treatment with griseofulvin." Br Med J (Clin Res Ed) 291 (1985):  249-50|Miyagawa S, Okuchi T, Shiomi Y, Sakamoto K "Subacute cutaneous lupus erythematosus lesions precipitated by griseofulvin." J Am Acad Dermatol 21 (1989):  343-6|Miyagawa S, Sakamoto K "Adverse reaction to griseofulvin in patients with circulating anti-ssa/ro and ssb/la autoantibodies." Am J Med 87 (1989):  99-102|"Product Information. Fulvicin P/G (griseofulvin)." Schering Corporation  (2002):', 'DDInter', 0);
