INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (55199, 1921, 'Heparin', 'Hyperkalemia', 'Heparin can suppress adrenal secretion of aldosterone and cause hyperkalemia, particularly in patients with diabetes mellitus, chronic renal failure, preexisting metabolic acidosis, or elevated serum potassium levels.  The risk of hyperkalemia appears to increase with duration of therapy, but is usually reversible.  Therapy with heparin should be administered cautiously in patients with preexisting hyperkalemia or risk factors for hyperkalemia.  Serum potassium should be measured before initiating treatment and also during treatment, especially if heparin is given for longer than 7 days.', '2', 'Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'DDInter', 0);
