INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (56907, 29851, 'Idarubicin', 'Heart Diseases', 'Idarubicin can cause myocardial toxicity leading to congestive heart failure.  Patients with preexisting heart disease or prior anthracycline therapy are at increased risk of congestive heart failure.  Close clinical monitoring of cardiac function, such as an electrocardiogram and/or determination of left ventricular ejection fraction, prior to each course of therapy is recommended.', '3', 'Chan-Lam D, Copplestone JA, Prentice A, Price R, Johnson S, Phillips M "Idarubicin cardiotoxicity in acute myeloid leukaemia." Lancet 340 (1992):  185-6|Bertelli G, Amoroso D, Pronzato P, Rosso R "Idarubicin: an evaluation of cardiac toxicity in 77 patients with solid tumors." Anticancer Res 8 (1988):  645-6|Villani F, Galimberti M, Comazzi R, Crippa F "Evaluation of cardiac toxicity of idarubicin (4- demethoxydaunorubicin)." Eur J Cancer Clin Oncol 25 (1989):  13-8|Gillies H, Liang R, Rogers H, Harper P, Parapia L, Cox G, Johnson S "Phase II trial of idarubicin in patients with advanced lymphoma." Cancer Chemother Pharmacol 21 (1988):  261-4|"Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
