INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57340, 6404, 'Imipramine', 'Alcoholic Intoxication', 'Tricyclic antidepressants can enhance the response to alcohol.  In patients who may use alcohol excessively, it should be borne in mind that the potentiation may increase the danger inherent in any suicide attempt or overdosage.', '2', '"Product Information. Pamelor (nortriptyline)." Sandoz Pharmaceuticals Corporation  (2002):|"Product Information. Elavil (amitriptyline)." Stuart Pharmaceuticals  (2002):|"Product Information. Norpramin (desipramine)." Hoechst Marion Roussel  (2002):|"Product Information. Tofranil (imipramine)." Novartis Pharmaceuticals  (2002):|"Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals  (2001):|"Product Information. Asendin (amoxapine)." Lederle Laboratories  (2001):|"Product Information. Surmontil (trimipramine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Vivactil (protriptyline)." Merck & Co., Inc  (2001):', 'DDInter', 0);
