INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57346, 6404, 'Imipramine', 'Neutropenia', 'The use of some tricyclic antidepressants has been associated with neutropenia (ANC < 500/mm3) and agranulocytosis (ANC < 500/mm3).  Leukocyte and differential blood counts should be performed in patients that develop fever and sore throat during treatment.  Therapy should be discontinued if there is evidence of pathologic neutrophil depression.', '2', '"Product Information. Pamelor (nortriptyline)." Sandoz Pharmaceuticals Corporation  (2002):|"Product Information. Elavil (amitriptyline)." Stuart Pharmaceuticals  (2002):|"Product Information. Norpramin (desipramine)." Hoechst Marion Roussel  (2002):|"Product Information. Tofranil (imipramine)." Novartis Pharmaceuticals  (2002):|"Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals  (2001):|"Product Information. Asendin (amoxapine)." Lederle Laboratories  (2001):|"Product Information. Surmontil (trimipramine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Vivactil (protriptyline)." Merck & Co., Inc  (2001):', 'DDInter', 0);
