INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57378, 22203, 'Anagrelide', 'Liver Diseases', 'Anagrelide undergoes extensive metabolism in the liver, with less than 1% recovered unchanged in the urine.  Therapy with anagrelide should be administered cautiously in patients with liver disease (bilirubin, SGOT, or other liver measurements greater than 1.5 times normal) and only when the benefits outweigh the risks.  Monitoring for signs of liver toxicity is recommended.', '3', '"Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57379, 28152, 'Anagrelide', 'Liver Diseases', 'Anagrelide undergoes extensive metabolism in the liver, with less than 1% recovered unchanged in the urine.  Therapy with anagrelide should be administered cautiously in patients with liver disease (bilirubin, SGOT, or other liver measurements greater than 1.5 times normal) and only when the benefits outweigh the risks.  Monitoring for signs of liver toxicity is recommended.', '3', '"Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57380, 22203, 'Anagrelide', 'Kidney Diseases', 'The use of anagrelide has been rarely associated with renal failure most often in patients with serum creatinine values greater than 2.0.  Therapy with anagrelide should be administered cautiously in patients with renal dysfunction.  Monitoring for signs of renal toxicity is recommended in these patients.', '3', '"Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57381, 28152, 'Anagrelide', 'Kidney Diseases', 'The use of anagrelide has been rarely associated with renal failure most often in patients with serum creatinine values greater than 2.0.  Therapy with anagrelide should be administered cautiously in patients with renal dysfunction.  Monitoring for signs of renal toxicity is recommended in these patients.', '3', '"Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57382, 22203, 'Anagrelide', 'Respiratory Insufficiency', 'The use of anagrelide may cause interstitial lung diseases, including allergic alveolitis, eosinophilic pneumonia, and interstitial pneumonitis.  The symptoms may present as progressive dyspnea with lung infiltrations.  Close monitoring should be exercised when using this agent in patients with pulmonary dysfunction.  It is recommended to discontinue anagrelide and evaluate the patient if symptoms of pulmonary toxicity occur.', '2', '"Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):', 'DDInter', 0);
