INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57585, 20894, 'Interferon alfa-n3', 'Depressive Disorder', 'Interferons (alfa or beta) can induce depression and suicidal behavior.  Suicidal attempts and suicides have been reported.  Therapy with interferons (alfa or beta) should be administered cautiously to patients with or predisposed to mental depression.  Clinical monitoring of psychological status is recommended.', '3', '"Product Information. Betaseron (interferon beta-1b)." Berlex Laboratories  (2002):|"Product Information. Avonex (interferon beta-1a)." Biogen  (2001):|"Product Information. Intron A (interferon alfa-2b)." Schering Corporation  (2001):|"Product Information. Infergen (interferon alfacon-1)." Amgen  (2001):|"Product Information. PEG-Intron (peginterferon alfa-2b)." Schering Corporation  (2001):|"Product Information. Sylatron (peginterferon alfa-2b)." Schering-Plough Corporation  (2015):|"Product Information. BESREMi (ropeginterferon alfa-2b)." PharmaEssentia USA Corp  (2021):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57586, 20894, 'Interferon alfa-n3', 'Hypothyroidism', 'Thyroid abnormalities, hypothyroidism or hyperthyroidism, have been reported in patients administered interferons.  Therapy with interferons should be administered cautiously to patients with thyroid dysfunction.  If thyroid control cannot be maintained within normal limits with medication, interferon-alfa should be discontinued.  Clinical monitoring of thyroid function is recommended.', '3', '"Product Information. Intron A (interferon alfa-2b)." Schering Corporation  (2001):|"Product Information. Infergen (interferon alfacon-1)." Amgen  (2001):|"Product Information. PEG-Intron (peginterferon alfa-2b)." Schering Corporation  (2001):|"Product Information. Sylatron (peginterferon alfa-2b)." Schering-Plough Corporation  (2015):|"Product Information. BESREMi (ropeginterferon alfa-2b)." PharmaEssentia USA Corp  (2021):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57587, 20894, 'Interferon alfa-n3', 'Diabetes Mellitus', 'Flu-like symptoms frequently associated with interferon-alfa therapy can precipitate ketoacidosis in diabetic patients.  Therapy with interferon alfa should be administered cautiously in patients with diabetes mellitus.  Clinical monitoring of blood glucose concentrations and anti-diabetic therapy is recommended.', '2', '"Product Information. Intron A (interferon alfa-2b)." Schering Corporation  (2001):|"Product Information. Infergen (interferon alfacon-1)." Amgen  (2001):|"Product Information. PEG-Intron (peginterferon alfa-2b)." Schering Corporation  (2001):|"Product Information. Sylatron (peginterferon alfa-2b)." Schering-Plough Corporation  (2015):', 'DDInter', 0);
