INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57611, 0, 'Iodide I-131', 'Kidney Diseases', 'Sodium iodide is known to be substantially excreted by the kidney, and the risk of toxic reactions to this drug may be greater in patients with impaired renal function.', '2', '"Product Information. Iodopen (sodium iodide)." Fujisawa  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57612, 0, 'Iodide I-131', 'Bone Marrow Failure Disorders', 'Sodium iodide-I-131 administered at doses used for thyroid chemotherapy induces myelosuppression including anemia, leukopenia, and thrombocytopenia.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Therapy with sodium iodide-I-131 should be administered cautiously in patients with compromised bone marrow reserve.  Clinical monitory of hematopoietic function is recommended.', '2', '"Product Information. Sodium Iodide 1-131 (sodium iodide I-131)." Mallinckrodt Medical Inc  (2022):', 'DDInter', 0);
