INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57648, 0, 'Iobenguane (I-123)', 'Hypertension', 'Iobenguane I-123 may increase release of norepinephrine from chromaffin granules and produce a transient episode of hypertension.  It is recommended to assess the patient''s pulse and blood pressure before and intermittently for 30 minutes after iobenguane I-123 administration.  Care should be exercised when using this agent in patients with hypertension.  Prior to Iobenguane I-123 administration, ensure emergency cardiac and anti-hypertensive treatments are readily available.', '2', '"Product Information. AdreView (iobenguane I-123)." GE Healthcare  (2022):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57649, 0, 'Iobenguane (I-123)', 'Parkinson Disease', 'Individuals with conditions that affect the sympathetic nervous system, e.g., Parkinsonian syndromes such as Parkinson''s disease or multiple system atrophy, may show decreased cardiac uptake of iobenguane I-123 independent of heart disease.', '2', '"Product Information. AdreView (iobenguane I-123)." GE Healthcare  (2022):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57650, 0, 'Iobenguane (I-123)', 'Kidney Diseases', 'Iobenguane I-123 is cleared by the kidneys and is not dialyzable.  When iobenguane I-123 is used in patients with severe renal impairment, the radiation dose may be increased due to delayed elimination of the drug which may also reduce the target to background rations and decrease the quality of scintigraphic images.  Care should be exercised when using this agent in patients with severe renal impairment as the safety and efficacy have not been established in these patients.  It is recommended to consider assessment of renal function in patients prior to iobenguane I-123 administration.', '2', '"Product Information. AdreView (iobenguane I-123)." GE Healthcare  (2022):', 'DDInter', 0);
