INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (57685, 24177, 'Ioversol', 'Acute Kidney Injury', 'Acute kidney injury, including renal failure, may occur after ioversol administration.  Risk factors include preexisting renal impairment, dehydration, diabetes, congestive heart failure, advanced vascular disease, multiple myeloma, elderly age, and concomitant use of nephrotoxic or diuretic medications.  The lowest dose of ioversol should be used in patients with renal impairment and other risk factors.', '2', '"Product Information. Optiray 350 (ioversol)." Mallinckrodt Medical Inc  (2006):', 'DDInter', 0);
