INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (206, 'Adwipril 4 mg 30 tab.', 'ادويبريل 4مجم 30 قرص', '45', '', 'Antihypertensive.ace', 'Perindopril', 'Adwia', 'Tab', 'أقراص', '4 mg', '3', '', 'about perindopril a long-acting dicarboxylate-containing ace inhibitor antihypertensive. mechanism of action of perindopril it is a prodrug and converted in to active perindoprilat; which is a long acting angiotensin converting enzyme inhibitor prevents t', '6223002977598', '', 1198, '2022-12-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Perindopril is a nonsulfhydryl prodrug that belongs to the angiotensin-converting enzyme (ACE) inhibitor class of medications. It is rapidly metabolized in the liver to perindoprilat, its active metabolite, following oral administration. Perindoprilat is a potent, competitive inhibitor of ACE, the enzyme responsible for the conversion of angiotensin I (ATI) to angiotensin II (ATII). ATII regulates blood pressure and is a key component of the renin-angiotensin-aldosterone system (RAAS). Perindopril may be used to treat mild to moderate essential hypertension, mild to moderate congestive heart failure, and to reduce the cardiovascular risk of individuals with hypertension or post-myocardial infarction and stable coronary disease.', '["C09BX01", "C09BA04", "C10BX11", "C10BX13", "C09AA04", "C09BX02", "C10BX12", "C09BB04", "C10BX15", "C10BX14", "C09BX04"]', '[]');
