INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (347, 'Alkanazole 150mg 1 caps.', 'الكانازول 150 مجم 1 كبسولة', '26', '17.64', 'Antifungals.triazoles', 'Fluconazole', 'Future pharmaceutical industries (fpi) > international trading office', 'Cap', 'كبسولة', '150mg', '1', '', 'about fluconazole a triazole derivative a board range antifungal. mechanism of action of fluconazole fluconazole is fungicidal or fungistatic depending on the drug concentrations. i t inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhi', '', '', 1860, '2024-11-10', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Fluconazole, commonly known as _Diflucan_, is an antifungal drug used for the treatment of both systemic and superficial fungal infections in a variety of tissues.  It was initially approved by the FDA in 1990. This drug is an _azole_ antifungal, in the same drug family as [ketoconazole] and [itraconazole]. Fluconazole has many advantages over the other antifungal drugs including the option of oral administration. The side effect profile of this drug is minimal. It has been demonstrated as an efficacious treatment for vaginal yeast infections in one single dose.[A174325]', '["J01RA07", "J02AC01", "D01AC15"]', '[]');
