INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (351, 'Diclopro k 50 mg 10 sachet', 'ديكلوبرو كيه 50مجم 10 اكياس', '50', '35', 'Nsaid.acetic acid derivatives', 'Diclofenac potassium', 'Atco pharma > xeedia pharma', 'Sachet', 'أكياس', '50 mg', '1', '', 'about diclofenac potassium nsaid anti-inflammatory antipyretic and analgesic. mechanism of action of diclofenac potassium diclofenac possess analgesic anti-inflammatory and antipyretic action. it inhibits the enzyme cyclo-oxygenase and there by inhibits t', '6224010509108', '', 1290, '2024-10-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diclofenac is a phenylacetic acid derivative and non-steroidal anti-inflammatory drug (NSAID).[label] NSAIDs inhibit cyclooxygenase (COX)-1 and-2 which are the enzyme responsible for producing prostaglandins (PGs). PGs contribute to inflammation and pain signalling. Diclofenac, like other NSAIDs, is often used as first line therapy for acute and chronic pain and inflammation from a variety of causes. Diclofenac was the product of rational drug design based on the structures of [phenylbutazone], [mefenamic acid], and [indomethacin].[A180796] The addition of two chlorine groups in the ortho position of the phenyl ring locks the ring in maximal torsion which appears to be related to increased potency. It is often used in combination with [misoprostol] to prevent NSAID-induced gastric ulcers. Diclofenac was first approved by the FDA in July 1988 under the trade name Voltaren, marketed by Novartis (previously Ciba-Geigy).[L7360]', '["S01CC01", "S01BC03", "M01AB55", "M02AA15", "M01AB05", "D11AX18"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (352, 'Exapine 60mg/0.6ml 2 prefilled syringes', 'اكسابين 60مجم/0.6مل 2 سرنجات معبأة', '218', '144', 'Anticoagulant.low mwt heparin', 'Enoxaparin sodium', 'Wadi elneel benta', 'Syringe', 'حقنة', '60mg', '2', '', 'about enoxaparin sodium a low molecular weight heparin anticoagulant. mechanism of action of enoxaparin sodium enoxaparin sodium is a low molecular weight heparin that accelerates formation of antithrombin iii- thrombin complex and deactivates thrombin an', '6224009259397', '', 2886, '2023-09-25', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Enoxaparin is a common low-molecular-weight heparin (LMWH) used in the prevention and management of various thromboembolic disorders. Initially approved by the FDA in 1993, it is administered by a subcutaneous or intravenous injection and marketed by several pharmaceutical companies.[L31393] Enoxaparin markedly reduces the incidence of venous thromboembolism in hospitalized patients when compared to unfractionated [heparin], without increasing the risk of serious bleeding.[A228178,A228313]', '["B01AB05"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (353, 'Alkapress 5mg 20 tab.', 'الكابرس 5مجم 20 قرص', '58', '41', 'Antihypertensive.selective calcium channel blocker', 'Amlodipine', 'Hikma pharma', 'Tab', 'أقراص', '5mg', '2', 'علاج ارتفاع ضغط الدم.', 'about amlodipine calcium channel blocker anti hypertensive anti angina. mechanism of action of amlodipine amlodipine is a second generation dihydropyridine ca channel blocker. it exerts it`s antihypertensive antianginal actions through blocking the influx', '6221000000041', '', 8601, '2024-08-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Amlodipine, initially approved by the FDA in 1987, is a popular antihypertensive drug belonging to the group of drugs called _dihydropyridine calcium channel blockers_.  Due to their selectivity for the peripheral blood vessels, dihydropyridine calcium channel blockers are associated with a lower incidence of myocardial depression and cardiac conduction abnormalities than other calcium channel blockers [A175327].

Amlodipine is commonly used in the treatment of high blood pressure and angina. Amlodipine has antioxidant properties and an ability to enhance the production of nitric oxide (NO), an important vasodilator that decreases blood pressure [A175321].  The option for single daily dosing of amlodipine is an attractive feature of this drug [FDA label].', '["C09XA54", "C10BX07", "C09BB03", "C09BB04", "C07FB07", "C09DB06", "C10BX18", "C07FB12", "C09DB05", "C09XA53", "C10BX03", "C07FB13", "C08CA01", "C09BX04", "C09BX03", "C09DB04", "C09DX03", "C08GA02", "C09DB07", "C09DX06", "C09BX01", "C10BX11", "C09DB02", "C10BX09", "C08CA51", "C09DX01", "C09DB09", "C09DB01", "C10BX14", "C09BB07", "C09DX07"]', '[]');
