INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (684, 'Amriphylline 100mg 5 supp.', 'امريفللين 100 مجم 5 لبوس', '4', '', 'Bronchodilator.phosphodiesterase inhibitors', 'Theophylline', 'Amriya', 'Supp', 'لبوس', '100mg', '1', 'لبوس موسع للشعب الهوائية -- يستخدم في حالات الكحة المصحوبة بالبلغم', 'about theophylline bronchodilator a methylxanthine derivative anti asthma in copd. mechanism of action of theophylline it inhibit phosphodiesterase enzyme which degrades cyclic nucleotides intracellularly and it results the cyclic amp accumulation in the', '6221075100073', '', 1319, '2022-12-02', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'A methylxanthine derivative from tea with diuretic, smooth muscle relaxant, bronchial dilation, cardiac and central nervous system stimulant activities. Mechanistically, theophylline acts as a phosphodiesterase inhibitor, adenosine receptor blocker, and histone deacetylase activator. Theophylline is marketed under several brand names such as Uniphyl and Theochron, and it is indicated mainly for asthma, bronchospasm, and COPD.', '["R03DA54", "R03DA74", "R03DA20", "R03DA04", "R03DB04"]', '[]');
