INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2229, 'Cefdin 300mg 10 caps.', 'سيفدين 300مجم 10 كبسولات', '63', '', 'Antibiotic.cephalosporin.third-generation', 'Cefdinir', 'Novartis', 'Cap', 'كبسولة', '300mg', '1', '', 'about cefdinir semisynthetic (third generation ) cephalosporin antibiotic oral broad-spectrum antibiotic. mechanism of action cefdinir is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negativ', '6223002642144', '', 1439, '2022-12-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Cefdinir, also known as Omnicef, is a semi-synthetic, broad-spectrum antibiotic belonging to the third generation of the cephalosporin class. It has been proven to be effective for the treatment of common bacterial infections in the ear, sinus, throat, lungs, and skin. Cefdinir was approved by the FDA in 1997 to treat a variety of mild to moderate infections and was initially marketed by Abbvie.[A180739] Because of its chemical structure, it is effective against organisms that are resistant to first-line cephalosporin therapy due to the production of beta-lactamase enzymes.[A180724,A180727]', '["J01DD15"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2232, 'Cefepime 500 mg i.m. i.v. vial (rowad)', 'سيفيبيم 500مجم فيال', '26', '', 'Antibiotic.cephalosporin.fourth-generation', 'Cefepime', 'Al rowad pharmaceutical industrial co.', 'Vial', 'فيال', '500 mg', '1', '', 'about cefepime fourth generation cephalosporin antibiotic. mechanism of action of cefepime cefepime is a 3rd generation cephalosporin which exerts its bactericidal action by inhibiting bacterial cell wall synthesis. cefepime inhibits transpeptidase & thus', '', '', 2052, '2022-12-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Cefepime is a fourth-generation cephalosporin antibiotic developed in 1994. Cefepime is active against Gram-positive and Gram-negative bacteria, and has greater activity against both compared to third-generation antibiotics.[A2457,A249050] Cefepime is normally used to treat severe nosocomial pneumonia and infections caused by multi-resistant microorganisms such as _Pseudomonas aeruginosa_, and is also indicated for the empirical treatment of febrile neutropenia.[A249050] The popularity of its third-generation predecessors, its clinical efficacy, and the high prevalence of multidrug-resistant bacteria might be some of the factors leading to an increase in the use of cefepime. The activity of cefepime against Enterobacteriaceae, _Pseudomonas aeruginosa_, and _Staphylococcus aureus_ is due to its high stability toward beta-lactamases.[A249050] In general, cefepime seems to be well tolerated; however, patients treated with this antibiotic, especially those with renal impairment, may develop neurotoxicity.[A249050,L42095,L42100]', '["J01DE01", "J01RA06"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2233, 'Cefidime 1 gm vial i.m./i.v.', 'سيفيديم 1 جرام حقن', '53', '36', 'Antibiotic', 'Ceftazidime', 'Eipico', 'Vial', 'فيال', '1 gm', '1', 'مضاد حيوي', 'about ceftazidime third generation cephalosporin antibiotic parenteral antibiotic. mechanism of action of ceftazidime ceftazidime is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms b', '6221032163998', '', 3284, '2025-04-28', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Bacteria possess a cell wall comprising a glycopeptide polymer commonly known as peptidoglycan, which is synthesized and remodelled through the action of a family of enzymes known as "penicillin-binding proteins" (PBPs).[A232920] β-lactam antibiotics, including cephalosporins, are PBP inhibitors that, through inhibition of essential PBPs, result in impaired cell wall homeostasis, loss of cell integrity, and ultimately bacterial cell death.[A232920, A232925, A232930] Ceftazidime is a third-generation cephalosporin with broad-spectrum antibacterial activity, including against some treatment-resistant bacteria such as _Pseudomonas aeruginosa_.[L32935]

Ceftazidime was approved by the FDA on July 19, 1985, and is currently available either alone or in combination with the non-β-lactam β-lactamase inhibitor [avibactam] to treat a variety of bacterial infections.[L32935, L32940]', '["J01DD02"]', '[]');
