INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2506, 'Chloroquine phosphate 200mg/5ml 5 amps', 'كلوروكين فوسفات 200مجم/5 مل 5 امبولات', '9', '', 'Antimalarial', 'Chloroquine phosphate', 'Alexandria', 'Amp', 'أمبول', '200mg', '5', '', 'about chloroquine 4- aminoquinoline excellent schizonticide antimalarial amebicide anti inflammatory and local irritant. mechanism of action of chloroquine malarial parasites digest their own haemoglobin and release highly toxic heme. this heme is used up', '', '', 966, '2023-05-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Chloroquine is an aminoquinolone derivative first developed in the 1940s for the treatment of malaria.[A191655] It was the drug of choice to treat malaria until the development of newer antimalarials such as [pyrimethamine], [artemisinin], and [mefloquine].[A191787] Chloroquine and its derivative [hydroxychloroquine] have since been repurposed for the treatment of a number of other conditions including HIV, systemic lupus erythematosus, and rheumatoid arthritis.[A192432]

