INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2607, 'Cipazole fort 1 gm 2 tab.', 'سيبازول فورت 1جم 2 قرص', '27', '17.5', 'Antiprotozoal', 'Secnidazole', 'Sigma > aspin pharmaceutical', 'Tab', 'أقراص', '1 gm', '1', 'مضاد للطفيليات والعدوى. ويستخدم لعلاج الديدان الدبوسية والدودة المستديرة والدودة السوطية والدودة الخطافية.', 'about secnidazole a nitroimidazole derivative antiprotozoal. mechanism of action of secnidazole secnidazole is a nitro imidazole which has broad spectrum cidal activity against protozoa and some anaerobic bacteria. its selective toxicity to anaerobic micr', '6221051913321', '', 4994, '2024-06-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Secnidazole is a second-generation 5-nitroimidazole antimicrobial agent. It is structurally related to other 5-nitroimidazoles, including [DB00916] and [DB00911], but displays improved oral absorption and a longer terminal elimination half-life than other drugs in this class. Secnidazole is selective against many anaerobic Gram-positive and Gram-negative bacteria as well as protozoa.[A27210] Once it enters bacteria and parasites, secnidazole is activated by bacterial or parasitic enzymes to form a radical anion, thereby damaging and killing the target pathogen.[A245503]

Secnidazole has been available in many other countries in Europe, Asia, South America, and Africa for decades.[A245503, A245508] In September 2017, FDA approved secnidazole under the market name Solosec for the treatment of trichomoniasis and bacterial vaginosis.[L39524]', '["P01AB07", "J01RA07"]', '[]');
