INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2885, 'Nercafix 40 mg 30 oro-dispersible films', 'نيركافيكس 40مجم 30 افلام ذائبة بالفم', '105', '75', 'Cns.stimulant.methylxanthine', 'Caffeine', 'Nerhadou international co.', 'Film', 'فيلم', '40 mg', '1', 'منبه للجهاز العصبي', 'about caffeine xanthine alkaloid central stimulant and metabolic stimulant. mechanism of action of caffeine because caffeine is both water-soluble and lipid-soluble it readily crosses the blood brain barrier .caffeine stimulates medullary vagal vasomotor', '6224011153256', '', 12894, '2024-11-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Caffeine is a drug of the methylxanthine class used for a variety of purposes, including certain respiratory conditions of the premature newborn, pain relief, and to combat drowsiness. Caffeine is similar in chemical structure to [Theophylline] and [Theobromine].[A187691,L9851] It can be sourced from coffee beans, but also occurs naturally in various teas and cacao beans, which are different than coffee beans.[T716] Caffeine is also used in a variety of cosmetic products and can be administered topically, orally, by inhalation, or by injection.[T716]

The caffeine citrate injection, used for apnea of the premature newborn, was initially approved by the FDA in 1999.[L9863] According to an article from 2017, more than 15 million babies are born prematurely worldwide. This correlates to about 1 in 10 births. Premature birth can lead to apnea and bronchopulmonary dysplasia, a condition that interferes with lung development and may eventually cause asthma or early onset emphysema in those born prematurely.[A187694] Caffeine is beneficial in preventing and treating apnea and bronchopulmonary dysplasia in newborns, improving the quality of life of premature infants.[T716]', '["D11AX26", "R03DA20", "V04CG30", "N06BC01"]', '[]');
