INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3927, 'Ruxotop 1.5% cream 60 gm', 'روكسو توب 1.5 % كريم 60 جم', '3,210', '2250', 'Vitiligo', 'Ruxolitinib', 'Marcyrl', 'Cream', 'كريم', '1.5%', '1', '', '', '6223003572563', '', 5150, '2024-10-24', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Ruxolitinib, formerly known as INCB018424 or INC424, is an anticancer drug and a Janus kinase (JAK) inhibitor. It is a potent and selective inhibitor of JAK1 and JAK2,[A229698] which are tyrosine kinases involved in cytokine signalling and hematopoiesis.[A7450] Myeloproliferative neoplasms, such as myelofibrosis and polycythemia vera, are often characterized by aberrant activation of the JAK-STAT pathway, leading to abnormal blood cell counts and thrombotic complications. By inhibiting JAK1 and JAK2, ruxolitinib works to block the dysregulated cell signalling pathways and prevents abnormal blood cell proliferation.[A229708] Due to a large number of patients with myeloproliferative neoplasms who have JAK2 mutations, ruxolitinib was the first ATP-competitive inhibitor of JAK1 and JAK2 ever developed.[A229938]
