INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3998, 'Rinvoq 30 mg 30 prolonged rel. tabs.', 'رينفوك 30مجم 30 قرص ممتد المفعول', '41,274', '', '', 'Upadacitinib', 'Abbvie scientific office', 'Tab', 'أقراص', '30 mg', '3', '', '', '', '', 982, '2025-06-02', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Upadacitinib is an oral Janus kinase (JAK)1-selective inhibitor and a disease-modifying antirheumatic drug (DMARD) used in the treatment of rheumatoid arthritis to slow down disease progression. Rheumatoid arthritis is a chronic autoimmune inflammatory disease affecting the peripheral joints. It is characterized by synovial inflammation and hyperplasia, autoantibody production, cartilage damage and bone destruction, leading to co-morbidities.[A189165] Despite a variety of therapeutic agents available for treatment, up to 40% of the patients do not respond to current therapies, including biological therapies.[A189171] The etiology of the disease is mostly unknown; however, the role of JAK as a driver of immune-mediated conditions was discovered, leading to the use of JAK as therapeutic targets for rheumatoid arthritis.[A189168] To reduce dose-related toxicity (as seen with some pan-JAK inhibitors) without significantly affecting efficacy, more selective JAK1 inhibitors, upadacitinib and [filgotinib], were developed.[A189165]
