INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4037, 'Egygastreaz 100mg/5ml pd. for oral 60ml susp', 'ايجي جاستريز 100 مجم/5مل معلق 60 مل', '31', '19.5', 'Anthelmentic', 'Nitazoxanide', 'Egpi', 'Susp', 'معلق', '100mg', '1', 'يستخدم هذا الدواء لعلاج الإسهال الناجم عن بعض أنواع العدوى الطفيلية, والاسهال الذي يعرف بإسم(إسهال المسافر).', '', '6223003930011', '', 4074, '2024-07-30', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Nitazoxanide belongs to the class of drugs known as _thiazolides_.  Nitazoxanide (NTZ) is a broad-spectrum anti-infective drug that markedly modulates the survival, growth, and proliferation of a range of extracellular and intracellular protozoa, helminths, anaerobic and microaerophilic bacteria, in addition to viruses. This drug is effective in the treatment of gastrointestinal infections including Cryptosporidium parvum or Giardia lamblia in healthy subjects. It is generally well tolerated. Nitazoxanide is a first-line, standard treatment for illness caused by C. parvum or G. lamblia infection in healthy (not immunosuppressed) adults and children and may also be considered in the treatment of illnesses caused by other protozoa or helminths [A31976].  Recently, this drug has been studied as a broad-spectrum antiviral agent due to its ability to inhibit the replication of several RNA and DNA viruses [A31977].', '["P01AX11"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4038, 'Egynir 125mg/5ml pd. for oral susp. 60ml', 'ايجينير 125مجم/5مل معلق 60 مل', '49', '32.5', 'Antibiotic.cephalosporin.third-generation', 'Cefdinir', 'Egyphar', 'Susp', 'معلق', '125mg', '1', 'مضاد حيوي يستخدم لعلاج التهاب الجيوب الأنفية--التهاب الأذن الوسطى--التهاب اللوزتين--الالتهاب الرئوي.', 'about cefdinir semisynthetic (third generation ) cephalosporin antibiotic oral broad-spectrum antibiotic. mechanism of action cefdinir is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negativ', '6224001026706', '', 1670, '2025-01-04', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Cefdinir, also known as Omnicef, is a semi-synthetic, broad-spectrum antibiotic belonging to the third generation of the cephalosporin class. It has been proven to be effective for the treatment of common bacterial infections in the ear, sinus, throat, lungs, and skin. Cefdinir was approved by the FDA in 1997 to treat a variety of mild to moderate infections and was initially marketed by Abbvie.[A180739] Because of its chemical structure, it is effective against organisms that are resistant to first-line cephalosporin therapy due to the production of beta-lactamase enzymes.[A180724,A180727]', '["J01DD15"]', '[]');
