INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4666, 'Levnoseiz 1000 mg 30 tabs', 'ليفنوسيز 1000مجم 30 قرص', '150', '', '', 'Levetiracetam', 'Gypto pharma for pharmaceutical', 'Tab', 'أقراص', '1000 mg', '3', '', '', '6224008874416', '', 1100, '2023-05-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Levetiracetam is a drug within the pyrrolidine class that is used to treat various types of seizures stemming from epileptic disorders. It was first approved for use in the United States in 1999 and is structurally and mechanistically unrelated to other anti-epileptic drugs (AEDs).[L8606,L8600,L8615] Levetiracetam possesses a wide therapeutic index[L8615,A185918] and little-to-no potential to produce, or be subject to, pharmacokinetic interactions[L8606,L8600,L8615] - these characteristics make it a desirable choice over other AEDs, a class of drugs notorious for having generally narrow therapeutic indexes and a propensity for involvement in drug interactions.[A185927]', '["N03AX14"]', '[]');
