INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4946, 'Regenflex bio-plus 75mg/3ml', 'ريجينفليكس بيو بلس 75مجم/3مل', '7,000', '', 'Intra articular injection', 'Hyaluronic acid', 'Regenyal laboratories', 'Unknown', 'غير محدد', '75mg', '1', '', '', '', '', 978, '2025-06-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Hyaluronic acid (HA) is an anionic, nonsulfated glycosaminoglycan found in connective, epithelial, and neural tissues; it was first isolated in 1934. Karl Meyer and John Palmer obtained glycosaminoglycan (GAG) from the bovine eye, giving it the name “hyaluronic acid”. HA is involved in many important physiological processes, including but not limited to wound healing, tissue regeneration, and joint lubrication. It demonstrates unique viscoelasticity, moisturizing, anti-inflammatory qualities, and other important properties that prove beneficial in various clinical applications.[A230128]  
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4947, 'Regenflex starter 32mg/2ml amp', 'ريجينفليكس ستارتر 32مجم/2مل امبول', '2,500', '', 'Intra articular injection', 'Hyaluronic acid sodium salt', 'Regenyal laboratories', 'Amp', 'أمبول', '32mg', '1', '', '', '', '', 886, '2025-06-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, NULL, NULL, NULL);
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4948, 'Melatonin 2 mg / 1 ml drops', 'ميلاتونين 2مجم / مل نقط فم', '180', '', 'Sleep aid', 'Melatonin', 'Zoser pharma', 'Drops', 'نقط', '2 mg', '1', '', '', '', '', 811, '2025-06-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Melatonin is a biogenic amine that is found in animals, plants and microbes. Aaron B. Lerner of Yale University is credited for naming the hormone and for defining its chemical structure in 1958.  In mammals, melatonin is produced by the pineal gland. The pineal gland is small endocrine gland, about the size of a rice grain and shaped like a pine cone (hence the name), that is located in the center of the brain (rostro-dorsal to the superior colliculus) but outside the blood-brain barrier. The secretion of melatonin increases in darkness and decreases during exposure to light, thereby regulating the circadian rhythms of several biological functions, including the sleep-wake cycle. In particular, melatonin regulates the sleep-wake cycle by chemically causing drowsiness and lowering the body temperature.  Melatonin is also implicated in the regulation of mood, learning and memory, immune activity, dreaming, fertility and reproduction. Melatonin is also an effective antioxidant.  Most of the actions of melatonin are mediated through the binding and activation of melatonin receptors. Individuals with autism spectrum disorders (ASD) may have lower than normal levels of melatonin. A 2008 study found that unaffected parents of individuals with ASD also have lower melatonin levels, and that the deficits were associated with low activity of the ASMT gene, which encodes the last enzyme of melatonin synthesis. Reduced melatonin production has also been proposed as a likely factor in the significantly higher cancer rates in night workers.', '["N05CH01"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4949, 'Suninevel 12.5mg 28 caps', 'سونينيفيل 12.5مجم 28 كبسولة', '3,137', '', 'Antineoplastic', 'Sunitinib', 'Hikma specialized pharmaceuticals', 'Cap', 'كبسولة', '12.5mg', '1', '', '', '', '', 728, '2025-06-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Sunitinib is a small-molecule multi-targeted receptor tyrosine kinase (RTK) inhibitor. On January 26, 2006, the agent was formally approved by the US FDA for the indications of treating renal cell carcinoma (RCC) and imatinib-resistant gastrointestinal stromal tumor (GIST). For these purposes, sunitinib is generally available as an orally administered formulation. Sunitinib inhibits cellular signaling by targeting multiple RTKs. These include all platelet-derived growth factor receptors (PDGF-R) and vascular endothelial growth factor receptors (VEGF-R). Sunitinib also inhibits KIT (CD117), the RTK that drives the majority of GISTs. In addition, sunitinib inhibits other RTKs including RET, CSF-1R, and flt3.', '["L01EX01"]', '[]');
